SELECT Customer.nfc_id, Customer.first_name, Customer.last_name, Customer.id_type, Customer.id_num, Customer.id_auth, Customer.birthdate, Space.space_id, Space.bed_count, Access.start_time, Access.end_time FROM Hotel.Customer, Hotel.Space, Hotel.Access, Hotel.Charge WHERE Customer.nfc_id=Access.nfc_id AND Access.space_id=Space.space_id AND Customer.nfc_id=Charge.nfc_id AND Space.bed_count>0 AND Charge.service_id LIKE 'Rm%';