SELECT Customer.nfc_id, Customer.first_name, Customer.last_name, Service.service_descr, Space.space_name, Space.space_descr FROM Customer, Visit, Space, Receive, Service, Provided WHERE CAST(Visit.entry_time as DATE)='$search1' AND Customer.nfc_id=Visit.nfc_id AND Visit.space_id=Space.space_id AND Customer.nfc_id=Receive.nfc_id AND Receive.service_id=Service.service_id AND Service.service_id=Provided.service_id AND Provided.space_id=Space.space_id;